#!/bin/bash ## define some things for cheetah proccessing #set $ds = chr(36) #set $amp = chr(38) #set $gt = chr(62) #set $lt = chr(60) #set $echo_cmd = 'echo' ## Find the defuse.pl in the galaxy tool path #import Cheetah.FileUtils ## declare a bash function for converting a results tsv into html with links to the get_reads output files results2html() { rlts=${ds}1 rslt_name=`basename ${ds}rlts` html=${ds}2 echo '${lt}html${gt}${lt}head${gt}${lt}title${gt}Defuse '${ds}rslt_name'${lt}/title${gt}${lt}/head${gt}${lt}body${gt}' ${gt} ${ds}html echo '${lt}h2${gt}Defuse '${ds}rslt_name'${lt}/h2${gt}${lt}table${gt}' ${gt}${gt} ${ds}html if [ -z "${ds}3" ] then awk '${ds}1 ~ /cluster_id/{printf("${lt}tr${gt}");for (i = 1; i ${lt}= NF; i++) {printf("${lt}th${gt}%s${lt}/th${gt}", ${ds}i);}; printf("${lt}/tr${gt}
");}\ ${ds}1 ~ /[1-9][0-9]*/{printf("${lt}tr${gt}");for (i = 1; i ${lt}= NF; i++) {printf("${lt}td${gt}%s${lt}/td${gt}", ${ds}i);}; printf("${lt}/tr${gt}
");}' ${ds}rlts ${gt}${gt} ${ds}html echo '${lt}/table${gt}' ${gt}${gt} ${ds}html echo '${lt}/body${gt}${lt}/html${gt}' ${gt}${gt} ${ds}html else export _EFP=${ds}3 mkdir -p ${ds}_EFP awk '${ds}1 ~ /cluster_id/{printf("${lt}tr${gt}");for (i = 1; i ${lt}= NF; i++) {printf("${lt}th${gt}%s${lt}/th${gt}", ${ds}i);}; printf("${lt}/tr${gt}
");}\ ${ds}1 ~ /[1-9][0-9]*/{fn="cluster_"${ds}1"_reads.txt"; \ printf("${lt}tr${gt}${lt}td${gt}${lt}a href="%s"${gt}%s${lt}/a${gt}${lt}/td${gt}",fn, ${ds}1);for (i = 2; i ${lt}= NF; i++) {printf("${lt}td${gt}%s${lt}/td${gt}", ${ds}i);}; printf("${lt}/tr${gt}
");}' ${ds}rlts ${gt}${gt} ${ds}html echo '${lt}/table${gt}' ${gt}${gt} ${ds}html echo '${lt}/body${gt}${lt}/html${gt}' ${gt}${gt} ${ds}html for i in `awk '${ds}1 ~ /[1-9][0-9]*/{print ${ds}1}' ${ds}rlts`; do fn=cluster_${ds}{i}_reads.txt; pn=${ds}_EFP/${ds}fn; perl \${DEFUSE_PATH}/scripts/get_reads.pl -c $defuse_config -o output_dir -i ${ds}i ${gt} ${ds}pn; done fi } ## substitute pathnames into config file if `grep __DEFUSE_PATH__ $defuse_config ${gt} /dev/null`;then sed -i'.tmp' "s#__DEFUSE_PATH__#\${DEFUSE_PATH}#" $defuse_config; fi if `grep __SAMTOOLS_BIN__ $defuse_config ${gt} /dev/null` ${amp}${amp} SAMTOOLS_BIN=`which samtools`;then sed -i'.tmp' "s#__SAMTOOLS_BIN__#\${SAMTOOLS_BIN}#" $defuse_config; fi if `grep __BOWTIE_BIN__ $defuse_config ${gt} /dev/null` ${amp}${amp} BOWTIE_BIN=`which bowtie`;then sed -i'.tmp' "s#__BOWTIE_BIN__#\${BOWTIE_BIN}#" $defuse_config; fi if `grep __BOWTIE_BUILD_BIN__ $defuse_config ${gt} /dev/null` ${amp}${amp} BOWTIE_BUILD_BIN=`which bowtie-build`;then sed -i'.tmp' "s#__BOWTIE_BUILD_BIN__#\${BOWTIE_BUILD_BIN}#" $defuse_config; fi if `grep __BLAT_BIN__ $defuse_config ${gt} /dev/null` ${amp}${amp} BLAT_BIN=`which blat`;then sed -i'.tmp' "s#__BLAT_BIN__#\${BLAT_BIN}#" $defuse_config; fi if `grep __FATOTWOBIT_BIN__ $defuse_config ${gt} /dev/null` ${amp}${amp} FATOTWOBIT_BIN=`which faToTwoBit`;then sed -i'.tmp' "s#__FATOTWOBIT_BIN__#\${FATOTWOBIT_BIN}#" $defuse_config; fi if `grep __GMAP_BIN__ $defuse_config ${gt} /dev/null` ${amp}${amp} GMAP_BIN=`which gmap`;then sed -i'.tmp' "s#__GMAP_BIN__#\${GMAP_BIN}#" $defuse_config; fi if `grep __GMAP_SETUP_BIN__ $defuse_config ${gt} /dev/null` ${amp}${amp} GMAP_SETUP_BIN=`which gmap_setup`;then sed -i'.tmp' "s#__GMAP_SETUP_BIN__#\${GMAP_SETUP_BIN}#" $defuse_config; fi if `grep __R_BIN__ $defuse_config ${gt} /dev/null` ${amp}${amp} R_BIN=`which R`;then sed -i'.tmp' "s#__R_BIN__#\${R_BIN}#" $defuse_config; fi if `grep __RSCRIPT_BIN__ $defuse_config ${gt} /dev/null` ${amp}${amp} RSCRIPT_BIN=`which Rscript`;then sed -i'.tmp' "s#__RSCRIPT_BIN__#\${RSCRIPT_BIN}#" $defuse_config; fi
## copy config to output cp $defuse_config $config_txt ## make a data_dir and ln -s the input fastq mkdir -p data_dir ## ln -s "$left_pairendreads" data_dir/reads_1.fastq ## ln -s "$right_pairendreads" data_dir/reads_2.fastq cp "$left_pairendreads" data_dir/reads_1.fastq cp "$right_pairendreads" data_dir/reads_2.fastq ## ln to output_dir in from_work_dir #if $defuse_out.__str__ != 'None': mkdir -p $defuse_out.dataset.extra_files_path ln -s $defuse_out.dataset.extra_files_path output_dir #else mkdir -p output_dir #end if ## run defuse.pl perl \${DEFUSE_PATH}/scripts/defuse.pl -name "$library_name" -c $defuse_config -1 data_dir/reads_1.fastq -2 data_dir/reads_2.fastq -o output_dir -p \$GALAXY_SLOTS; exit_code_for_galaxy=\$?; ## copy primary results to output datasets if [ -e output_dir/log/defuse.log ]; then cp output_dir/log/defuse.log $defuse_log; fi ## if [ -e output_dir/results.tsv ]; then cp output_dir/results.tsv $results_tsv; fi if [ -e output_dir/results.filtered.tsv ]; then cp output_dir/results.filtered.tsv $results_filtered_tsv; fi if [ -e output_dir/results.classify.tsv ]; then cp output_dir/results.classify.tsv $results_classify_tsv; fi #if $breakpoints_bam: if [ -e output_dir/results.filtered.tsv ] ${amp}${amp} [ -e output_dir/breakpoints.genome.psl ] then awk "\$10 ~ /^(`awk '\$1 ~ /[0-9]+/{print \$1}' output_dir/results.filtered.tsv | tr '
' '|'`)\$/{print \$0}" output_dir/breakpoints.genome.psl > breakpoints.genome.filtered.psl ${amp}${amp} psl2sam.pl breakpoints.genome.filtered.psl > breakpoints.genome.filtered.sam ${amp}${amp} samtools view -b -T /panfs/roc/rissdb/galaxy/genomes/NCBIM37/defuse/defuse.reference.fa -o breakpoints.genome.filtered.bam breakpoints.genome.filtered.sam ${amp}${amp} samtools sort breakpoints.genome.filtered.bam breakpoints ${amp}${amp} ## samtools index breakpoints.bam cp breakpoints.bam $fusions_bam fi #end if ## create html with links for output_dir #if $defuse_out.__str__ != 'None': if [ -e $defuse_out ] then echo '${lt}html${gt}${lt}head${gt}${lt}title${gt}Defuse Output${lt}/title${gt}${lt}/head${gt}${lt}body${gt}' ${gt} $defuse_out echo '${lt}h2${gt}Defuse Output Files${lt}/h2${gt}${lt}ul${gt}' ${gt}${gt} $defuse_out pushd $defuse_out.dataset.extra_files_path for f in `find -L . -maxdepth 1 -type f`; do fn=`basename ${ds}f`; echo '${lt}li${gt}${lt}a href="'${ds}fn'"${gt}'${ds}fn'${lt}/a${gt}${lt}/li${gt}' ${gt}${gt} $defuse_out; done popd echo '${lt}/ul${gt}' ${gt}${gt} $defuse_out echo '${lt}/body${gt}${lt}/html${gt}' ${gt}${gt} $defuse_out fi #end if ## run get_reads.pl on each cluster #if $fusion_reads.__str__ != 'None': if [ -e output_dir/results.filtered.tsv -a -e $fusion_reads ] then mkdir -p $fusion_reads.dataset.extra_files_path results2html output_dir/results.filtered.tsv $fusion_reads $fusion_reads.dataset.extra_files_path fi #end if (exit \$exit_code_for_galaxy)
